import { use, useEffect, useState } from 'react';
import './App.css';


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

async function getBestMove(board, aiSymbol){
  const res = await fetch("/api/best-move", {
    method: "POST",
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify({ board, ai: aiSymbol}),

  });
  if(!res.ok) throw new Error(await res.text());
  return await res.json();
}

function Square({ value, onSquareClick, disabled}) {
  const isDown = value !== null;

  return (
    <button 
      className={`btn-3d ${isDown ? "down" : ""}`} 
      onClick = {onSquareClick} 
      disabled = {disabled || isDown}>
      {value}
    </button>
  );
}

function Board({xIsNext, squares, onPlay, aiEnabled, aiSymbol }) {
  const [aiThinking, setAiThinking] = useState(false);



    function handleClick(i) {

    if (aiThinking) return;
    if (squares[i] || calculateWinner(squares)) {
      return;
    }

    const nextSquares = squares.slice();

    nextSquares[i] = xIsNext ? "X" : "O"

    onPlay(nextSquares)
  }

  useEffect(()=>{

    let cancelled = false;

    const winner = calculateWinner(squares);
    const boardFull = squares.every(Boolean);

    if(!aiEnabled || aiThinking || winner || boardFull) return;



  const turn = xIsNext? "X" : "O"
  if(turn !== aiSymbol) return; 

  (async () => {
    try{
      setAiThinking(true);
      const data = await getBestMove(squares, aiSymbol);

      if (cancelled) return;

      if(data.move == null) return;

      const nextSquares = squares.slice();
      nextSquares[data.move] = aiSymbol;
      onPlay(nextSquares);

    } finally{
      if(!cancelled) setAiThinking(false);
    }
  })();
  return () => {cancelled = true;} ;
}, [aiEnabled, aiThinking, squares, xIsNext, onPlay]);

  return (
    <div className='app'>
        <div className="board-row">
          <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
          <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
          <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
        </div>
        <div className="board-row">
          <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
          <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
          <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
        </div>
        <div className="board-row">
          <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
          <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
          <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
        </div>
      </div>
  );
}


export default function App(){

  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);

  const [aiEnabled,setAiEnabled] = useState(true);

  const aiSymbol = "O";

  const xIsNext = currentMove % 2 === 0 ;
  const squares = history[currentMove];

  function handlePlay(nextSquares){
    const nextHistory= [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
    }

    const winner = calculateWinner(squares);
    const status = winner
    ? `Winner: ${winner}`
    : `Next player: ${xIsNext ? "X" : "O"}`;

    return (
      <div>
        <h2>{status}</h2>

        <label style={{display : "inline-flex", gap: 8, alignItems: "center"}}>
          <input
          type = "checkbox"
          checked = {aiEnabled}
          onChange={(e) => setAiEnabled(e.target.checked)}
          />
          Play vs ai
          </label>

          <Board
            xIsNext={xIsNext}
            squares={squares}
            onPlay={handlePlay}
            aiEnabled={aiEnabled}
            aiSymbol={aiSymbol}
            />
      </div>
    );
}
