import { use, useEffect, useState, useRef } from 'react';
import './App.css';


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

async function getBestMove(board, aiSymbol){
  const res = await fetch("/api/best-move", {
    method: "POST",
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify({ board, ai: aiSymbol}),

  });
  if(!res.ok) throw new Error(await res.text());
  return await res.json();
}

function Square({ value, onSquareClick, disabled}) {
  const isDown = value !== null;

  return (
    <button 
      className={`btn-3d ${isDown ? "down" : ""}`} 
      onClick = {onSquareClick} 
      disabled = {disabled || isDown}>
      {value}
    </button>
  );
}



function Board({ xIsNext, squares, onPlay, aiEnabled, aiSymbol }) {
  const [aiThinking, setAiThinking] = useState(false);
  const aiInFlight = useRef(false);

  useEffect(() => {
    const winner = calculateWinner(squares);
    const boardFull = squares.every(s => s !== null);

    if (!aiEnabled || winner || boardFull) return;

    const turn = xIsNext ? "X" : "O";
    if (turn !== aiSymbol) return;

    if (aiInFlight.current) return;
    aiInFlight.current = true;

    (async () => {
      try {
        setAiThinking(true);

        const data = await getBestMove(squares, aiSymbol);
        if (data.move == null) return;

        const nextSquares = squares.slice();
        nextSquares[data.move] = aiSymbol;
        onPlay(nextSquares);
      } finally {
        aiInFlight.current = false;
        setAiThinking(false);
      }
    })();
  }, [aiEnabled, squares, xIsNext, onPlay, aiSymbol]);

  function handleClick(i) {
    if (aiThinking) return;
    if (squares[i] || calculateWinner(squares)) return;

    const nextSquares = squares.slice();
    nextSquares[i] = xIsNext ? "X" : "O";
    onPlay(nextSquares);
  }

  return (
    <div className="app">
      {[0,1,2,3,4,5,6,7,8].map(i => (
        <Square
          key={i}
          value={squares[i]}
          onSquareClick={() => handleClick(i)}
          disabled={aiThinking}
        />
      ))}
    </div>
  );
}


export default function App(){

  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);

  const [aiEnabled,setAiEnabled] = useState(true);

  const aiSymbol = "O";

  const xIsNext = currentMove % 2 === 0 ;
  const squares = history[currentMove];

  function handlePlay(nextSquares){
    const nextHistory= [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
    }

    const winner = calculateWinner(squares);
    const status = winner
    ? `Winner: ${winner}`
    : `Next player: ${xIsNext ? "X" : "O"}`;

    return (
      <div>
        <h2>{status}</h2>

        <label style={{display : "inline-flex", gap: 8, alignItems: "center"}}>
          <input
          type = "checkbox"
          checked = {aiEnabled}
          onChange={(e) => setAiEnabled(e.target.checked)}
          />
          Play vs ai
          </label>

          <Board
            xIsNext={xIsNext}
            squares={squares}
            onPlay={handlePlay}
            aiEnabled={aiEnabled}
            aiSymbol={aiSymbol}
            />
      </div>
    );
}
